package oj_1;

import java.util.Scanner;
/**
 * a+b
 */
public class Test1_a_plus_b {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner cin = new Scanner(System.in);
		while(cin.hasNext()){
			int a = cin.nextInt();
			int b = cin.nextInt();
		    System.out.println(a+b);
		}
		
	}

}
